                        <input type='text' {...register('telefonoCelular',{
                            required: true,
                        })}/>
                    </div>
                </div>
                <div>
                    <div className='ContenedorCampoFormulario'>
                        Direccion: <br/>
                        Departamento:
                        <select {...register('departamento')} onChange={handleChange}>
                            {/* Renderizar primero opciones de departamentos */}
                            {departamentos.map(
                                (departamento) =>
